// emailService.js
const nodemailer = require('nodemailer');

// Function to send email
const sendEmail = async (to, subject, text) => {
  // Create a transporter object using your SMTP settings
  const transporter = nodemailer.createTransport({
    host: 'smtp.example.com', // Replace with your SMTP server
    port: 587, // Replace with your SMTP port (usually 587 for TLS)
    secure: false, // true for 465, false for other ports
    auth: {
      user: 'nsatyasaicharan@example.com', // Replace with your email address
      pass: 'CharanCharan@01@01', // Replace with your email password
    },
  });

  // Set up email data
  const mailOptions = {
    from: '"Admin" <nsatyasaicharan@example.com>', // Sender address
    to, // List of recipients
    subject, // Subject line
    text, // Plain text body
  };

  // Send the email
  try {
    await transporter.sendMail(mailOptions);
    console.log('Email sent successfully!');
  } catch (error) {
    console.error('Error sending email:', error);
  }
};

// Export the sendEmail function
module.exports = { sendEmail };
