import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './MyOrders.css'; // Include a CSS file for styling

const MyOrders = () => {
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const token = localStorage.getItem('token'); // Get the token from localStorage
        const response = await axios.get('http://localhost:5000/my-orders', {
          headers: {
            Authorization: `Bearer ${token}`, // Ensure the token is sent
          },
        });

        const formattedOrders = response.data.map(order => ({
          serviceName: order.serviceId?.serviceName || 'Unknown Service', // Service name
          serviceImage: order.serviceId?.image || '', // Assuming service has an image field
          serviceDescription: order.serviceId?.description || 'No description available', // Description
          status: order.status || 'Pending', // Status of the order
          createdAt: new Date(order.createdAt).toLocaleString(), // Formatted creation date
        }));

        setOrders(formattedOrders); // Set orders into state
      } catch (error) {
        console.error('Error fetching orders:', error);
      }
    };

    fetchOrders();
  }, []); // Fetch orders once on component mount

  return (
    <div className="my-orders-container">
      <h1>My Orders</h1>
      {orders.length === 0 ? (
        <p>No orders found.</p>
      ) : (
        <div className="orders-grid">
          {orders.map((order, index) => (
            <div key={index} className="order-card">
              <img
                src={`http://localhost:5000/uploads/${order.serviceImage}`}
                alt={order.serviceName}
                className="service-image"
              />
              <div className="order-details">
                <h2>{order.serviceName}</h2>
                <p>{order.serviceDescription}</p>
                <p>Status: <strong>{order.status}</strong></p>
                <p>Order Date: {order.createdAt}</p>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default MyOrders;
