import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode'; // Use named import
import './Navbar.css';

function Navbar() {
  const navigate = useNavigate();
  const token = localStorage.getItem('token');
  let userRole = null;

  // Decode token if it exists
  if (token) {
    const decodedToken = jwtDecode(token); // Use jwtDecode here
    userRole = decodedToken.role; // Extract the role from the token
  }

  const isAuthenticated = !!token;

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/');
    window.location.reload(); // Optional: Refresh navbar state
  };

  return (
    <nav className="navbar">
      <h2>Gardening App</h2>
      <ul>
        <li>
          <Link to="/">Home</Link>
        </li>
        <li>
          <Link to="/packages">Packages</Link>
        </li>

        {/* Show "Add Services" and "Review Orders" only for Admins */}
        {isAuthenticated && userRole === 'admin' && (
          <>
            <li>
              <Link to="/add-service">Add Services</Link>
            </li>
            <li>
              <Link to="/review-orders">Review Orders</Link>
            </li>
          </>
        )}

        {!isAuthenticated ? (
          <>
            <li>
              <Link to="/login">Login</Link>
            </li>
            <li>
              <Link to="/signup">Signup</Link>
            </li>
          </>
        ) : (
          <li>
            <button onClick={handleLogout} style={{ cursor: 'pointer' }}>Logout</button>
          </li>
        )}
      </ul>
    </nav>
  );
}

export default Navbar;
