import React, { useEffect, useState } from 'react';
import axios from 'axios';

function ReviewOrders() {
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const response = await axios.get('http://localhost:5000/review-orders');
        setOrders(response.data);
      } catch (error) {
        alert('Error fetching orders: ' + error.message);
      }
    };

    fetchOrders();
  }, []);

  const handleAcceptOrder = async (orderId) => {
    try {
      const response = await axios.post(`http://localhost:5000/accept-order/${orderId}`);
      alert(response.data.message);
      // Optionally, refetch orders or update state here
    } catch (error) {
      alert('Error accepting order: ' + error.message);
    }
  };

  return (
    <div className="review-orders-container">
      <h2>Review Orders</h2>
      {orders.length === 0 ? (
        <p>No orders to review.</p>
      ) : (
        orders.map(order => (
          <div key={order.id} className="order-card">
            <h4>Order ID: {order.id}</h4>
            <p>Customer: {order.customerName}</p>
            <button onClick={() => handleAcceptOrder(order.id)}>Accept Order</button>
          </div>
        ))
      )}
    </div>
  );
}

export default ReviewOrders;
