import React, { useState, useEffect } from 'react';
import { Link, useHistory } from 'react-router-dom';
import './Navbar.css';

function Navbar({ isAdmin }) {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const history = useHistory();

  // Check login state when the component mounts
  useEffect(() => {
    // Here we are simulating login state from localStorage/sessionStorage
    const loggedIn = localStorage.getItem('isLoggedIn');
    setIsLoggedIn(loggedIn === 'true'); // Convert string to boolean
  }, []);

  // Handle logout
  const handleLogout = () => {
    // Clear user data and login status from localStorage or session
    localStorage.removeItem('isLoggedIn');
    setIsLoggedIn(false); // Update state
    history.push('/login'); // Redirect to login page after logout
  };

  return (
    <nav className="navbar">
      <h2>Gardening App</h2>
      <ul>
        <li>
          <Link to="/">Home</Link>
        </li>
        <li>
          <Link to="/packages">Packages</Link>
        </li>

        {!isLoggedIn ? (
          <>
            <li>
              <Link to="/login">Login</Link>
            </li>
            <li>
              <Link to="/signup">Signup</Link>
            </li>
          </>
        ) : (
          <li>
            <button onClick={handleLogout} className="logout-button">
              Logout
            </button>
          </li>
        )}

        {isAdmin && isLoggedIn && ( // Only show admin links if isAdmin is true and user is logged in
          <>
            <li>
              <Link to="/add-service">Add Services</Link>
            </li>
            <li>
              <Link to="/review-orders">Review Orders</Link>
            </li>
          </>
        )}
      </ul>
    </nav>
  );
}

export default Navbar;
