import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './MyOrders.css'; // Import the CSS file for styling

const MyOrders = () => {
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get('http://localhost:5000/my-orders', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        const formattedOrders = response.data.map(order => ({
          serviceName: order.serviceName || 'Unknown Service',
          area: order.area || 'N/A',
          actionOne: order.actionOne,
          actionTwo: order.actionTwo,
          cost: order.cost,
          status: order.status || 'Pending',
          createdAt: new Date(order.createdAt).toLocaleString(),
          specialRequests: order.specialRequests || 'None',
        }));

        setOrders(formattedOrders);
      } catch (error) {
        console.error('Error fetching orders:', error);
      }
    };

    fetchOrders();
  }, []);

  return (
    <div className="orders-container">
      <h1>My Orders</h1>
      <div className="orders-list">
        {orders.length === 0 ? (
          <p>No orders found.</p>
        ) : (
          orders.map((order, index) => (
            <div className="order-card" key={index}>
              <h3>Service: {order.serviceName}</h3>
              <p>Area: {order.area} sq ft</p>
              <p>Action One: {order.actionOne}</p>
              <p>Action Two: {order.actionTwo}</p>
              <p>Cost: ${order.cost}</p>
              <p>Status: {order.status}</p>
              <p>Date: {order.createdAt}</p>
              <p>Special Requests: {order.specialRequests}</p>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default MyOrders;
