import React, { useState, useEffect } from 'react';
import axios from 'axios';

function BookService({ serviceId }) {
  const [phoneNumber, setPhoneNumber] = useState('');
  const [email, setEmail] = useState('');
  const [area, setArea] = useState('');
  const [specialRequests, setSpecialRequests] = useState('');
  const [message, setMessage] = useState('');

  useEffect(() => {
    // Get the email from localStorage or a user context (assuming the user is logged in)
    const userEmail = localStorage.getItem('userEmail'); // Save user's email in localStorage during login
    if (userEmail) {
      setEmail(userEmail);
    }
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const token = localStorage.getItem('token'); // Get token from localStorage

    if (!phoneNumber || !area) {
      setMessage('Please fill in all required fields.');
      return;
    }

    try {
      const response = await axios.post('http://localhost:5000/request-service', {
        serviceId,
        phoneNumber,
        email,
        area,
        specialRequests,
      }, {
        headers: {
          'Authorization': `Bearer ${token}`, // Include token in headers
        },
      });
      setMessage('Service booked successfully!');
      // Optionally clear the form
      setPhoneNumber('');
      setArea('');
      setSpecialRequests('');
    } catch (error) {
      console.error(error);
      setMessage('Error booking service. Please try again.');
    }
  };

  return (
    <div className="book-service-container">
      <h2>Book Service</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Phone Number:</label>
          <input 
            type="text" 
            value={phoneNumber} 
            onChange={(e) => setPhoneNumber(e.target.value)} 
            required 
          />
        </div>

        <div>
          <label>Email:</label>
          <input 
            type="email" 
            value={email} 
            readOnly 
          />
        </div>

        <div>
          <label>Area (in sq feet):</label>
          <input 
            type="number" 
            value={area} 
            onChange={(e) => setArea(e.target.value)} 
            required 
          />
        </div>

        <div>
          <label>Special Requests:</label>
          <textarea 
            value={specialRequests} 
            onChange={(e) => setSpecialRequests(e.target.value)} 
          />
        </div>

        <button type="submit">Book Service</button>
      </form>

      {message && <p>{message}</p>}
    </div>
  );
}

export default BookService;
