const handleAcceptOrder = async (orderId) => {
    const token = localStorage.getItem('token');
    try {
      const response = await axios.post(`http://localhost:5000/accept-order/${orderId}`, {}, {
        headers: {
          Authorization: `Bearer ${token}`, // Include token
        },
      });

      // Send email notification
      const userEmail = 'user@example.com'; // Fetch user's email from your database or the order info
      await sendEmail(userEmail, 'Service Request Accepted', 'Your service request has been accepted!');

      alert(response.data.message);
      fetchOrders(); // Refetch orders after accepting
    } catch (error) {
      alert('Error accepting order: ' + error.message);
    }
  };

  const handleRejectOrder = async (orderId) => {
    const token = localStorage.getItem('token');
    try {
      const response = await axios.post(`http://localhost:5000/reject-order/${orderId}`, {}, {
        headers: {
          Authorization: `Bearer ${token}`, // Include token
        },
      });

      // Send email notification
      const userEmail = 'user@example.com'; // Fetch user's email from your database or the order info
      await sendEmail(userEmail, 'Service Request Rejected', 'Your service request has been rejected.');

      alert(response.data.message);
      fetchOrders(); // Refetch orders after rejecting
    } catch (error) {
      alert('Error rejecting order: ' + error.message);
    }
  };

  return (
    <div className="review-orders-container">
      <h2>Review Orders</h2>
      {orders.length === 0 ? (
        <p>No orders to review.</p>
      ) : (
        orders.map(order => (
          <div key={order._id} className="order-card">
            <h4>Order ID: {order._id}</h4>
            <p>Customer: {order.userId.username}</p> {/* Assuming userId holds user info */}
            <button onClick={() => handleAcceptOrder(order._id)}>Accept Order</button>
            <button onClick={() => handleRejectOrder(order._id)}>Reject Order</button>
          </div>
        ))
      )}
    </div>
  );
}

export default ReviewOrders;
